mapper.select:
SELECT tbl2.*,
(SELECT COUNT(*) FROM sns_like WHERE bno = tbl2.bno) AS like_cnt
FROM
	(
	SELECT ROWNUM AS rn, tbl.*
		FROM
		(
		SELECT * FROM snsboard
		ORDER BY bno DESC
		) tbl
	) tbl2

WHERE rn > #{pageStart} 
AND rn <= #{pageEnd}

SELECT bno
FROM sns_like
WHERE user_id = #{userId}
